INSERT INTO questionnaire_heart_voices (questionnaire_response_id, questionnaire_id, user_id, content, word_count, category, tags, emotion_score, emotion_category, is_public, is_approved, status, submission_type, anonymous_nickname, ip_address, user_agent, created_at, updated_at) VALUES (NULL, 'questionnaire_9', 'c526d74a-ab2f-4989-8f25-f1277b5422e3', '实习期间最大的收获是学会了如何在真实的工作环境中应用所学知识。理论和实践确实有很大差距，但正是这种差距让我快速成长。导师很耐心，同事们也很友善，整个团队氛围很好。通过参与实际项目，我不仅提升了专业技能，更重要的是培养了职场素养和团队协作能力。', 121, 'campus-life', '["求职心得","经验分享","实习体验","计算机类","医学类"]', 0.6499639161373758, 'negative', true, true, 'archived', 'semi-anonymous', '应届毕业生', '192.168.36.132', 'Mozilla/5.0 (compatible; TestDataGenerator)', '2024-02-22 08:29:56', '2024-09-30 22:09:46');
INSERT INTO questionnaire_heart_voices (questionnaire_response_id, questionnaire_id, user_id, content, word_count, category, tags, emotion_score, emotion_category, is_public, is_approved, status, submission_type, anonymous_nickname, ip_address, user_agent, created_at, updated_at) VALUES (NULL, 'questionnaire_9', '4ef3af7c-887e-422e-8a29-5830dccc8e28', '回到家乡工作是一个深思熟虑的决定。虽然一线城市机会更多，但家乡的发展潜力也很大，而且能够陪伴家人，生活成本相对较低。重要的是要结合个人情况和职业规划，选择最适合自己的发展道路。', 88, 'workplace-adaptation', '["待定中","工程技术","理科类","求职心得","二线城市"]', 0.1622299502769846, 'positive', true, true, 'active', 'semi-anonymous', NULL, '192.168.55.0', 'Mozilla/5.0 (compatible; TestDataGenerator)', '2024-04-12 03:54:16', '2024-04-30 10:18:54');
INSERT INTO questionnaire_heart_voices (questionnaire_response_id, questionnaire_id, user_id, content, word_count, category, tags, emotion_score, emotion_category, is_public, is_approved, status, submission_type, anonymous_nickname, ip_address, user_agent, created_at, updated_at) VALUES (NULL, 'questionnaire_9', '82b91a68-a590-4395-9ef3-2ace67119381', '分享一下最近的面试经历，希望对大家有帮助。整个面试过程分为三轮：首先是HR初筛，主要了解基本情况和期望薪资；然后是技术面试，考察专业技能和项目经验；最后是总监面试，更多关注个人发展规划和团队协作能力。面试过程中要保持自信，诚实回答问题，同时展现自己的学习能力和适应能力。', 135, 'campus-life', '["继续深造","工程技术","医学类"]', -0.5897115128846098, 'negative', false, true, 'pending', 'semi-anonymous', '创业者', '192.168.135.14', 'Mozilla/5.0 (compatible; TestDataGenerator)', '2024-04-19 00:01:15', '2024-04-21 23:53:02');
INSERT INTO questionnaire_heart_voices (questionnaire_response_id, questionnaire_id, user_id, content, word_count, category, tags, emotion_score, emotion_category, is_public, is_approved, status, submission_type, anonymous_nickname, ip_address, user_agent, created_at, updated_at) VALUES (NULL, 'questionnaire_7', '057a2030-ef5b-4915-8bcc-f1767b9e7d69', '技能提升是一个持续的过程，需要制定明确的学习计划和目标。我通过在线课程、实践项目和同行交流不断提升自己的专业能力。在快速变化的行业中，持续学习是保持竞争力的关键，不仅要跟上技术趋势，还要注重基础知识的巩固。', 103, 'internship-experience', '["人生感悟","二线城市","求职中","工程技术","面试经历"]', -0.7984334889591831, 'negative', false, true, 'pending', 'anonymous', '职场新人', '192.168.151.244', 'Mozilla/5.0 (compatible; TestDataGenerator)', '2024-10-05 06:47:04', '2024-08-25 13:35:16');
INSERT INTO questionnaire_heart_voices (questionnaire_response_id, questionnaire_id, user_id, content, word_count, category, tags, emotion_score, emotion_category, is_public, is_approved, status, submission_type, anonymous_nickname, ip_address, user_agent, created_at, updated_at) VALUES (NULL, 'questionnaire_2', '1507dac4-e3e5-4e78-9ebf-29133a1c11ae', '刚入职场时确实有很多不适应的地方，从学校的自由环境到公司的规范制度，需要一个适应过程。最重要的是要保持学习的心态，多观察多请教。职场和学校最大的区别是责任感和团队协作，要学会从个人学习转向团队目标。', 99, 'employment-feedback', '["回乡就业","教育类","创业中","待定中"]', 0.8955574020316064, 'neutral', true, true, 'active', 'anonymous', '自由职业者', '192.168.85.204', 'Mozilla/5.0 (compatible; TestDataGenerator)', '2024-12-17 13:44:18', '2024-10-08 10:34:12');
